package TreePackage;/*	An interface for the ADT binary tree.	COMPSCI 105	Assignment 2 - Part 2	Name: 	UPI: */public interface BinaryTreeInterface<T> extends TreeInterface<T>,                                                 TreeIteratorInterface<T>{  /* Sets an existing binary tree to a new one-node binary tree */  public void setTree(T rootData);    /* Sets an existing binary tree to a new binary tree.      rootData is an object that is the data in the new tree's root      leftTree is the left subtree of the new tree      rightTree is the right subtree of the new tree */  public void setTree(T rootData, BinaryTreeInterface<T> leftTree,                                  BinaryTreeInterface<T> rightTree);} // end BinaryTreeInterface